<!DOCTYPE html>
<html lang="en">
  <head>
    <meta charset="utf-8">
    <meta http-equiv="X-UA-Compatible" content="IE=edge">
    <meta name="viewport" content="width=device-width, initial-scale=1">
    <!-- The above 3 meta tags *must* come first in the head; any other head content must come *after* these tags -->
    <title>Bootstrap 101 Template</title>

    <!-- Bootstrap -->
    <link href="bootstrap/dist/css/bootstrap.min.css" rel="stylesheet">

    <!-- HTML5 shim and Respond.js for IE8 support of HTML5 elements and media queries -->
    <!-- WARNING: Respond.js doesn't work if you view the page via file:// -->
    <!--[if lt IE 9]>
      <script src="https://oss.maxcdn.com/html5shiv/3.7.2/html5shiv.min.js"></script>
      <script src="https://oss.maxcdn.com/respond/1.4.2/respond.min.js"></script>
    <![endif]-->
  </head>
  <body>

    <div class="container">
      <div class="row flex-column">
        <div class="col-xs-12">
          <div class="text-center login-title">
            <span class="glyphicon glyphicon-user"></span> USER LOGIN
          </div>
          <form id="login-form" class="login-container">
              <div class="alert alert-danger" id="login-warning" style="display:none;">
                <strong>Login Failed!</strong> Username = youre , Password = awesome
              </div>
              <span class="glyphicon glyphicon-user"></span> Username
              <input id="username" type="text" name="username"/>
              <span class="glyphicon glyphicon-lock"></span> Password
              <input id="password" type="password" name="password"/>
              <div class="remember-me-container">
                <input id="remember_user" type="checkbox" name="remember"/> 
                <div class="remember-me">Remember Me</div>
              </div>
              <input id="btn" type="submit" value="Log In"/>
          </form>
        </div>
      </div>
    </div>
   
   
   
   
   
   
   
   
   
   
   


    <!-- jQuery (necessary for Bootstrap's JavaScript plugins) -->
    <script src="https://ajax.googleapis.com/ajax/libs/jquery/1.11.3/jquery.min.js"></script>
    <!-- Include all compiled plugins (below), or include individual files as needed -->
    <script src="bootstrap/dist/js/bootstrap.min.js"></script>
    
    
    <script>
    
    $( document ).ready(function() {
      
      var login_form = $('#login-form');
      var login_warning = $('#login-warning');
      var username = $('#username');
      var password = $('#password');
      var remember_user = $('#remember_user');
      
      
      login_form.submit(function(e){
        e.preventDefault();
        ajaxCall();
      });
        
      
      function ajaxCall(){
        $.ajax({
          method: "POST",
          url: "php/data-grab.php?s=001",
          dataType: "json",
          data: {
            username: username.val(),
            password: password.val(),
            remember_user: remember_user.val()
          },
          success: function(data){
            console.log(data.auth);
            if (!data.auth) {
              login_warning.fadeIn();
            }
          }
          
        });
      }; 
      
    });//ready
    </script>
  </body>
</html>